mapper.select:
SELECT user_name
FROM users
WHERE user_id = #{userId}
  AND password = #{password}

SELECT user_name
FROM users
WHERE age = #{age}

mapper.insert:
INSERT INTO users (
    user_id, user_name, password, age, grade, reg_date
) VALUES (
    #{userId}, #{userName}, #{password}, #{age}, #{grade}, SYSDATE
)

mapper.update:
UPDATE users
SET user_name = #{userName},
    password = #{password},
    age = #{age},
    grade = #{grade}
WHERE user_id = #{userId}
